// constants
const int MAX_CHANNELS = 64;


// declaration of chugin constructor
CK_DLL_CTOR( ambpanacn_ctor );
// declaration of chugin desctructor
CK_DLL_DTOR( ambpanacn_dtor );

// declaration of setters
