// declaration of getters
CK_DLL_MFUN( ambpanacn_getAzimuth );
CK_DLL_MFUN( ambpanacn_getElevation );
CK_DLL_MFUN( ambpanacn_getOrder );
CK_DLL_MFUN( ambpanacn_getOutChannels );

